INSERT INTO public.content_entries (title, slug, data, status, published_at, version, seo_data, created_at, updated_at, id, author_id, content_type_id) VALUES ('Entry page', 'shipping', '{"page_key": "shipping", "template": "landing", "sections": [{"type": "page_header", "heading": "Shipping Information", "subheading": "Fast & Reliable Delivery"}, {"type": "rich_text", "content": "<p>We offer multiple shipping options to get your beauty products to you as quickly as possible.</p><h3>Standard Shipping</h3><p>Free on orders over $50. Delivery in 5-7 business days.</p><h3>Express Shipping</h3><p>$9.99 flat rate. Delivery in 2-3 business days.</p><h3>Next Day Delivery</h3><p>$19.99 flat rate. Order by 2pm for next day delivery.</p>"}, {"type": "rich_text", "content": "<h3>International Shipping</h3><p>We ship to over 50 countries worldwide. International shipping rates are calculated at checkout based on your location and order weight.</p><p>Please note that international orders may be subject to customs duties and taxes, which are the responsibility of the recipient.</p>"}, {"type": "rich_text", "content": "<h3>Order Tracking</h3><p>Once your order ships, you will receive a confirmation email with a tracking number. You can track your order status in your account dashboard or using the link provided in the email.</p>"}]}', 'published', NULL, 1, '{"seo_title": "Shipping Information | Bakalr Boutique", "seo_description": "Learn about our shipping options, delivery times, and shipping costs."}', '2025-12-06 15:05:23.947716+00', '2025-12-06 15:05:23.947716+00', '0dac4613-79ad-44dd-8836-d4c0e285c6ba', NULL, 'aae2f4b5-d099-41f4-b72e-a17947ec6a34');
