INSERT INTO public.content_entries (title, slug, data, status, published_at, version, seo_data, created_at, updated_at, id, author_id, content_type_id) VALUES ('Entry page', 'careers', '{"page_key": "careers", "template": "landing", "sections": [{"type": "page_header", "heading": "Join Our Team", "subheading": "Build Your Career in Beauty"}, {"type": "rich_text", "content": "<p>At Bakalr Boutique, we are passionate about beauty and empowering our team members to grow.</p><h3>Why Work With Us</h3><p>We believe in creating an inclusive, supportive environment where creativity thrives.</p><h4>Benefits</h4><ul><li>Competitive salary and commission</li><li>Employee discount on all products</li><li>Health and wellness benefits</li><li>Professional development opportunities</li><li>Flexible scheduling</li></ul>"}, {"type": "rich_text", "content": "<h3>Open Positions</h3><p>We are always looking for talented individuals to join our team. Current openings include:</p><ul><li><strong>Beauty Advisor</strong> - In-store customer service</li><li><strong>E-commerce Specialist</strong> - Digital marketing and sales</li><li><strong>Warehouse Associate</strong> - Fulfillment and logistics</li><li><strong>Customer Service Representative</strong> - Support team</li></ul>"}, {"type": "cta", "heading": "Ready to Apply?", "description": "Send your resume to careers@bakalrboutique.com", "button_text": "Email Us", "button_url": "mailto:careers@bakalrboutique.com"}]}', 'published', NULL, 1, '{"seo_title": "Careers at Bakalr Boutique | Join Our Team", "seo_description": "Join our passionate team at Bakalr Boutique. Explore career opportunities in beauty retail."}', '2025-12-06 15:05:24.760491+00', '2025-12-06 15:05:24.760491+00', '271193ed-fcf9-4a99-a765-764f68994d3f', NULL, 'aae2f4b5-d099-41f4-b72e-a17947ec6a34');
