INSERT INTO public.content_entries (title, slug, data, status, published_at, version, seo_data, created_at, updated_at, id, author_id, content_type_id) VALUES ('Entry page', 'about', '{"page_key": "about", "template": "landing", "meta_title": "About Us - Bakalr Boutique | Our Story & Mission", "meta_description": "Learn about Bakalr Boutique - your trusted destination for premium hair care, beauty products, and accessories. Discover our mission to celebrate beauty in all its forms.", "sections": [{"type": "hero", "banner_key": "hero-about", "full_width": true}, {"type": "rich_text", "content": "<h2>Who We Are</h2><p>Bakalr Boutique was founded with a simple yet powerful mission: to make premium beauty products accessible to everyone while celebrating the diverse beauty of our community.</p><p>What started as a small family business in 2020 has grown into a trusted destination for hair care, skincare, and beauty essentials. We noticed a gap in the market for quality products that cater to diverse hair types and skin tones, and we set out to fill it.</p><p>Today, we proudly serve customers across the United States and internationally, offering carefully curated products from both established brands and emerging innovators in the beauty industry.</p>"}, {"type": "rich_text", "content": "<h2>Our Mission</h2><p><em>\"To empower every individual to embrace their unique beauty through access to premium, diverse, and ethically sourced beauty products.\"</em></p><p>We believe that beauty has no single definition. It comes in all colors, textures, and forms. Our mission is to provide the products, education, and support that help you look and feel your best\u2014whatever that means to you.</p>"}, {"type": "rich_text", "content": "<h2>Our Values</h2><ul><li><strong>Quality First:</strong> We only carry products we believe in and have tested ourselves. Every item in our store meets our high standards for quality, safety, and effectiveness.</li><li><strong>Diversity & Inclusion:</strong> Beauty comes in all forms, and we celebrate every one of them. Our product selection reflects the diverse needs of our community.</li><li><strong>Community Support:</strong> We actively support Black-owned businesses, women-owned brands, and local entrepreneurs. A portion of our proceeds goes to community initiatives.</li><li><strong>Sustainability:</strong> We partner with eco-conscious brands and continuously work to minimize our environmental footprint through responsible packaging and shipping practices.</li><li><strong>Education:</strong> We believe in empowering our customers with knowledge. Our blog, tutorials, and customer service team are here to help you make informed decisions.</li></ul>"}, {"type": "rich_text", "content": "<h2>Why Choose Bakalr Boutique?</h2><ul><li><strong>Curated Selection:</strong> Every product is hand-picked by our team of beauty experts</li><li><strong>Authentic Products:</strong> 100% genuine products sourced directly from authorized distributors</li><li><strong>Expert Support:</strong> Our knowledgeable team is here to help you find the perfect products</li><li><strong>Fast Shipping:</strong> Most orders ship within 24-48 hours</li><li><strong>Easy Returns:</strong> 30-day hassle-free return policy</li><li><strong>Loyalty Rewards:</strong> Earn points on every purchase and unlock exclusive perks</li></ul>"}, {"type": "cta", "heading": "Ready to Transform Your Beauty Routine?", "subheading": "Discover our curated collection of premium products", "button_text": "Shop Now", "button_url": "/products", "secondary_button_text": "Contact Us", "secondary_button_url": "/contact", "background_color": "bg-primary", "text_color": "text-white"}]}', 'published', NULL, 3, '{"seo_title": "About Us - Bakalr Boutique", "seo_description": "Learn about Bakalr Boutique''s mission to provide premium beauty supplies."}', '2025-12-05 03:20:43.229633+00', '2025-12-08 02:00:23.406578+00', 'f6910471-d67a-4890-9b53-39548be4b224', '3fc3c7fa-2faf-44