INSERT INTO public.translations (translated_data, status, source_locale, translation_service, quality_score, is_manual, version, created_at, updated_at, id, content_entry_id, locale_id) VALUES ('{"page_key": "shipping", "template": "landing", "sections": [{"type": "_en-t\u00eate de page", "heading": "Informations sur l''exp\u00e9dition", "subheading": "Livraison rapide et fiable"}, {"type": "texte_rich", "content": "<p>Nous offrons plusieurs options d''exp\u00e9dition pour obtenir vos produits de beaut\u00e9 le plus rapidement possible.</p><h3>Livraison standard</h3><p> Gratuit sur les commandes de plus de 50 $. Livraison en 5-7 jours ouvrables.</p><h3>Livraison express</h3><p> 9,99 $ tarif forfaitaire. Livraison en 2-3 jours ouvrables.</p><h3>Livraison du jour suivant</h3><p>19,99 $ tarif forfaitaire. Commandez avant 14h pour la livraison du jour suivant.</p>"}, {"type": "texte_rich", "content": "<h3>International Livraison</h3><p>Nous exp\u00e9dions dans plus de 50 pays \u00e0 travers le monde. Les taux d''exp\u00e9dition internationaux sont calcul\u00e9s \u00e0 la caisse en fonction de votre emplacement et du poids de votre commande.</p><p> Veuillez noter que les commandes internationales peuvent \u00eatre assujetties aux droits de douane et aux taxes, qui sont de la responsabilit\u00e9 du destinataire.</p>"}, {"type": "texte_rich", "content": "<h3>Suivi de la commande</h3><p> Une fois votre commande exp\u00e9di\u00e9e, vous recevrez un email de confirmation avec un num\u00e9ro de suivi. Vous pouvez suivre l''\u00e9tat de votre commande dans votre tableau de bord de compte ou en utilisant le lien fourni dans le courriel.</p>"}]}', 'completed', NULL, 'libretranslate', 0.95, false, 1, '2025-12-06 15:05:23.957036+00', '2025-12-06 15:05:23.957036+00', '5ef2e3d8-c514-4665-9146-1cf7a0e0c092', '0dac4613-79ad-44dd-8836-d4c0e285c6ba', '8f36bfaa-ef7f-482a-ade0-8932044ec4a5');
INSERT INTO public.translations (translated_data, status, source_locale, translation_service, quality_score, is_manual, version, created_at, updated_at, id, content_entry_id, locale_id) VALUES ('{"page_key": "shipping", "template": "landing", "sections": [{"type": "seite_header", "heading": "Versandinformationen", "subheading": "Schnelle und zuverl\u00e4ssige Lieferung"}, {"type": "reich_text", "content": "<p>Wir bieten mehrere Versandoptionen an, um Ihre Beauty-Produkte so schnell wie m\u00f6glich an Sie zu bekommen.</p>h3>Standard Shipping</h3>p>Free auf Bestellungen \u00fcber $50. Lieferung in 5-7 Werktagen.</p>h3>Express Shipping</h3>p>$9.99 Flachpreis. Lieferung in 2-3 Werktagen.</p>h3>N\u00e4chste Tag Lieferung</h3>p>$19.99 Pauschalsatz. Bestellung um 2pm f\u00fcr den n\u00e4chsten Tag Lieferung.</p>"}, {"type": "reich_text", "content": "<h3>International Shipping</h3>p>p>Wir liefern in \u00fcber 50 L\u00e4nder weltweit. Internationaler Versand wird bei der Kasse nach Standort und Bestellgewicht berechnet.</p>p> Bitte beachten Sie, dass internationale Bestellungen Z\u00f6lle und Steuern unterliegen, die dem Empf\u00e4nger obliegen.</p>"}, {"type": "reich_text", "content": "<h3>Order Tracking</h3>p> Sobald Ihre Bestellung Schiffe, erhalten Sie eine Best\u00e4tigungs-E-Mail mit einer Tracking-Nummer. Sie k\u00f6nnen Ihren Bestellstatus in Ihrem Konto Dashboard verfolgen oder den in der E-Mail angegebenen Link verwenden.</p>"}]}', 'completed', NULL, 'libretranslate', 0.95, false, 1, '2025-12-06 15:05:23.957036+00', '2025-12-06 15:05:23.957036+00', 'f137b66a-ff69-4daa-becb-f0ad39ead532', '0dac4613-79ad-44dd-8836-d4c0e285c6ba', '2e87ede3-3b54-4690-ab93-70ca567d6d75');
INSERT INTO public.translations (translated_data, status, source_locale, translation_service, quality_score, is_manual, version, created_at, updated_at, id, content_entry_id, locale_id) VALUES ('{"page_key": "shipping", "template": "landing", "sections": [{"type": "page_header", "heading": "Informaci\u00f3n de env\u00edo", "subheading": "Entrega r\u00e1pida y fiable"}, {"type": "rich_text", "content": "Nos ofrecemos m\u00faltiples opciones de env\u00edo para obtener sus productos de belleza a usted lo m\u00e1s r\u00e1pido posible.Seguido/p Nombramiento garantizadoh3 Fuerte env\u00edo realizado/h3 Fuertegidop Sin pedidos superiores a $50. Entrega en 5-7 d\u00edas h\u00e1biles.Seguido/p instrucciones indicadah3 prendaExpress Env\u00edo efectuado/h3 t\u00edtulo recomendadop]9.99 tarifa plana. Entrega en 2-3 d\u00edas h\u00e1biles.Seguido/p] indicah3 contactoEl siguiente d\u00eda de entrega se indica/h3 t\u00edtulo recomendadop]$19.99 tarifa plana. Ordenar por 2pm para el d\u00eda siguiente de entrega"}, {"type": "rich_text", "content": "\" No 3 \" International Enviamos a m\u00e1s de 50 pa\u00edses de todo el mundo. Las tarifas de env\u00edo internacional se calculan a partir de su ubicaci\u00f3n y el peso del pedido. Tenga en cuenta que las \u00f3rdenes internacionales pueden estar sujetas a aranceles e impuestos aduaneros, que son responsabilidad del destinatario"}, {"type": "rich_text", "content": "\u25a0h3 curso de order Una vez que su pedido se env\u00eda, recibir\u00e1 un correo electr\u00f3nico de confirmaci\u00f3n con un n\u00famero de seguimiento. Puede rastrear su estado de pedido en el panel de su cuenta o utilizando el enlace proporcionado en el correo electr\u00f3nico"}]}', 'completed', NULL, 'libretranslate', 0.95, false, 1, '2025-12-06 15:05:23.957036+00', '2025-12-06 15:05:23.957036+00', 'd4661b15-fe93-4911-a13d-1e013d9f7d00', '0dac4613-79ad-44dd-8836-d4c0e285c6ba', '3c424c7b-6a9e-48ac-b66a-e8972087b9ae');
INSERT INTO public.translations (translated_data, status, source_locale, translation_service, quality_score, is_manual, version, created_at, updated_at, id, content_entry_id, locale_id) VALUES ('{"page_key": "returns", "template": "landing", "sections": [{"type": "_en-t\u00eate de page", "heading": "Retours et \u00e9changes", "subheading": "Retours sans probl\u00e8me"}, {"type": "texte_rich", "content": "<p>Nous voulons que vous aimiez votre achat. Si vous n''\u00eates pas compl\u00e8tement satisfait, nous sommes l\u00e0 pour vous aider.</p><h3>Politique de retour</h3><p> Nous acceptons les retours dans les 30 jours suivant l''achat de la plupart des articles dans leur \u00e9tat original et non ouvert.</p><h4>Articles admissibles</h4><ul><li>Produits non ouverts et non utilis\u00e9s</li><li>Produits dans leur emballage initial</li><li>Articles munis d''\u00e9tiquettes</li></ul><h4>Articles non retourn\u00e9s</h4><ul><li> Produits de beaut\u00e9 ouverts (pour des raisons d''hygi\u00e8ne) Articles de vente marqu\u00e9s comme vente finale</li><li>Cartes-cadeaux</li></ul>"}, {"type": "texte_rich", "content": "<h3>Comment retourner</h3><ol><li>Se connecter \u00e0 votre compte et aller \u00e0 l''historique de commande</li><li> S\u00e9lectionnez la commande et les \u00e9l\u00e9ments que vous souhaitez retourner</li><li> Imprimer l''\u00e9tiquette de retour pr\u00e9pay\u00e9e</li><li>Emballez les articles en toute s\u00e9curit\u00e9 et d\u00e9posez-les \u00e0 n''importe quel endroit d''exp\u00e9dition</li></ol><p> Les remboursements sont trait\u00e9s dans les 5-7 jours ouvrables suivant la r\u00e9ception de votre d\u00e9claration.</p>"}, {"type": "texte_rich", "content": "<h3>\u00c9changes</h3><p> Besoin d''une autre teinte ou taille? Nous sommes heureux d''\u00e9changer votre article. Suivez le m\u00eame processus de retour et s\u00e9lectionnez \u00c9changer comme votre raison. Nous exp\u00e9dierons votre nouvel article d\u00e8s que nous aurons re\u00e7u votre retour.</p>"}]}', 'completed', NULL, 'libretranslate', 0.95, false, 1, '2025-12-06 15:05:24.365458+00', '2025-12-06 15:05:24.365458+00', '77c9b347-25ce-4a2d-8fac-ae12264cf1b6', '7fbc19d6-8fd2-4f35-b51e-7eeb62056300', '8f36bfaa-ef7f-482a-ade0-8932044ec4a5');
INSERT INTO public.translations (translated_data, status, source_locale, translation_service, quality_score, is_manual, version, created_at, updated_at, id, content_entry_id, locale_id) VALUES ('{"page_key": "returns", "template": "landing", "sections": [{"type": "seite_header", "heading": "R\u00fccksendungen und B\u00f6rsen", "subheading": "Hassle-Free Returns"}, {"type": "reich_text", "content": "<p>Wir m\u00f6chten, dass Sie Ihren Kauf lieben. Wenn Sie nicht vollst\u00e4ndig zufrieden sind, sind wir hier, um zu helfen. Wir akzeptieren R\u00fccksendungen innerhalb von 30 Tagen nach dem Kauf f\u00fcr die meisten Positionen in ihrem urspr\u00fcnglichen, unge\u00f6ffneten Zustand.</p>h4>Eligible Items</h4>>ul>li>Uner\u00f6ffnete und nichtverwendete Produkte</li>>Le>N-Returnable Items Offene Sch\u00f6nheitsprodukte (aus Hygienegr\u00fcnden)</li>li> Waren, die als Endverkauf gekennzeichnet sind </li>li>Giftkarten</li>/ul>"}, {"type": "reich_text", "content": "<h3>How to Return</h3>ol>li>Log in Ihr Konto und gehen Sie zu Order History</li>li> W\u00e4hlen Sie die Bestellung und die Gegenst\u00e4nde aus, die Sie zur\u00fcckgeben m\u00f6chten </li>li> Drucken Sie die vorbezahlte R\u00fcckgabemarke </li>li>Pack Artikel sicher und fallen Sie an jedem Versandort ab </li>/ol>p> R\u00fcckerstattungen werden innerhalb von 5-7 Werktagen nach Erhalt Ihrer Rendite verarbeitet.</p>"}, {"type": "reich_text", "content": "<h3>Exchanges</h3>p> Brauchen Sie eine andere Farbe oder Gr\u00f6\u00dfe? Wir freuen uns, Ihren Artikel auszutauschen. Folgen Sie dem gleichen R\u00fcckgabeprozess und w\u00e4hlen Sie Exchange wie Ihr Grund. Wir versenden Ihren neuen Artikel, sobald wir Ihre R\u00fccksendung erhalten.</p>"}]}', 'completed', NULL, 'libretranslate', 0.95, false, 1, '2025-12-06 15:05:24.365458+00', '2025-12-06 15:05:24.365458+00', '0a9096f1-1944-450b-8690-63a0bb299d0f', '7fbc19d6-8fd2-4f35-b51e-7eeb62056300', '2e87ede3-3b54-4690-ab93-70ca567d6d75');
