INSERT INTO public.translations (translated_data, status, source_locale, translation_service, quality_score, is_manual, version, created_at, updated_at, id, content_entry_id, locale_id) VALUES ('{"question": "Wie erstelle ich ein Konto?", "answer": "Klicken Sie auf das Benutzersymbol in der Top-Navigation und w\u00e4hlen Sie \"Sign Up\" oder \"Register\". Geben Sie Ihre E-Mail ein, erstellen Sie ein Passwort und f\u00fcllen Sie Ihre Daten aus. Mit einem Konto k\u00f6nnen Sie Bestellungen verfolgen, Favoriten speichern, und Check-out schneller.", "category": "navigation", "display_order": 5}', 'completed', NULL, 'libretranslate', 0.95, false, 1, '2025-12-08 00:43:32.97741+00', '2025-12-08 00:43:32.97741+00', '9bf9a02a-6cb3-42d4-962c-b32d5154b505', '3bb45a70-fc8e-4010-951b-c7080fd55849', '2e87ede3-3b54-4690-ab93-70ca567d6d75');
INSERT INTO public.translations (translated_data, status, source_locale, translation_service, quality_score, is_manual, version, created_at, updated_at, id, content_entry_id, locale_id) VALUES ('{"question": "\u00bfC\u00f3mo puedo crear una cuenta?", "answer": "Haga clic en el icono del usuario en la navegaci\u00f3n superior y seleccione \"Registrarse\" o \"Registrarse\". Introduce tu correo electr\u00f3nico, crea una contrase\u00f1a y llena tus datos. Tener una cuenta te permite seguir \u00f3rdenes, guardar favoritos, y checkout m\u00e1s r\u00e1pido.", "category": "navigation", "display_order": 5}', 'completed', NULL, 'libretranslate', 0.95, false, 1, '2025-12-08 00:43:32.97741+00', '2025-12-08 00:43:32.97741+00', '256c5a65-bb34-4235-aee1-b3757ae3b090', '3bb45a70-fc8e-4010-951b-c7080fd55849', '3c424c7b-6a9e-48ac-b66a-e8972087b9ae');
INSERT INTO public.translations (translated_data, status, source_locale, translation_service, quality_score, is_manual, version, created_at, updated_at, id, content_entry_id, locale_id) VALUES ('{"question": "Comment puis-je lire les avis de produits?", "answer": "Sur n''importe quelle page de produit, faites d\u00e9filer vers le bas pour voir les commentaires et les \u00e9valuations des clients. Vous pouvez voir la note des \u00e9toiles, lire les commentaires d\u00e9taill\u00e9s, et voir combien de personnes ont trouv\u00e9 chaque avis utile. Cliquez sur la note \u00e9toiles pour voir toutes les critiques pour ce produit.", "category": "navigation", "display_order": 6}', 'completed', NULL, 'libretranslate', 0.95, false, 1, '2025-12-08 00:43:41.809926+00', '2025-12-08 00:43:41.809926+00', 'e56d6bb7-fcfb-4032-af02-21f747984d26', 'b3c2af72-7eda-43ac-be52-09c3ab06ea56', '8f36bfaa-ef7f-482a-ade0-8932044ec4a5');
INSERT INTO public.translations (translated_data, status, source_locale, translation_service, quality_score, is_manual, version, created_at, updated_at, id, content_entry_id, locale_id) VALUES ('{"question": "Wie lese ich Produktbewertungen?", "answer": "Auf jeder Produktseite, Scrollen Sie nach unten, um Kundenbewertungen und Bewertungen zu sehen. Sie k\u00f6nnen die Sternebewertung sehen, detaillierte Bewertungen lesen und sehen, wie viele Menschen fanden jede Bewertung hilfreich. Klicken Sie auf die Sternebewertung, um alle Bewertungen f\u00fcr dieses Produkt anzuzeigen.", "category": "navigation", "display_order": 6}', 'completed', NULL, 'libretranslate', 0.95, false, 1, '2025-12-08 00:43:41.809926+00', '2025-12-08 00:43:41.809926+00', 'fe750b67-fa2f-4ae3-8505-cf84504d5bbc', 'b3c2af72-7eda-43ac-be52-09c3ab06ea56', '2e87ede3-3b54-4690-ab93-70ca567d6d75');
INSERT INTO public.translations (translated_data, status, source_locale, translation_service, quality_score, is_manual, version, created_at, updated_at, id, content_entry_id, locale_id) VALUES ('{"page_key": "about", "template": "landing", "meta_title": "About Us - Bakalr Boutique | Our Story & Mission", "meta_description": "Learn about Bakalr Boutique - your trusted destination for premium hair care, beauty products, and accessories. Discover our mission to celebrate beauty in all its forms.", "sections": [{"type": "held", "banner_key": "heldenverwandtschaft", "full_width": true}, {"type": "reich_text", "content": "<h2>Who We Are</h2>p>Bakalr Boutique wurde mit einer einfachen und dennoch leistungsf\u00e4higen Mission gegr\u00fcndet: Premium-Sch\u00f6nheitsprodukte f\u00fcr alle zug\u00e4nglich zu machen, w\u00e4hrend die verschiedenen Sch\u00f6nheiten unserer Gemeinschaft gefeiert werden. Was im Jahr 2020 als kleines Familienunternehmen begann, ist zu einem vertrauensw\u00fcrdigen Ziel f\u00fcr Haarpflege, Hautpflege und Sch\u00f6nheitswesen geworden. Wir bemerkten eine L\u00fccke im Markt f\u00fcr Qualit\u00e4tsprodukte, die unterschiedlichen Haartypen und Hautt\u00f6nen entsprechen, und wir stellten fest, sie zu f\u00fcllen.</p>p> Heute dienen wir stolz Kunden in den Vereinigten Staaten und international und bieten sorgf\u00e4ltig kuratierte Produkte von etablierten Marken und aufstrebenden Innovatoren in der Sch\u00f6nheitsindustrie.</p>"}, {"type": "reich_text", "content": "<h2>Unsere Mission</h2>p>>em>\"Um jedes Individuum zu bef\u00e4higen, seine einzigartige Sch\u00f6nheit durch den Zugang zu hochwertigen, vielf\u00e4ltigen und ethisch bedingten Sch\u00f6nheitsprodukten zu erfassen.\"</em>/p>p> Wir glauben, dass Sch\u00f6nheit keine einzige Definition hat. Es kommt in allen Farben, Texturen und Formen. Unsere Mission ist es, die Produkte, Bildung und Unterst\u00fctzung zu bieten, die Ihnen helfen, Ihr Bestes zu sehen und zu f\u00fchlen \u2013 was auch immer das f\u00fcr Sie bedeutet.</p>"}, {"type": "reich_text", "content": "<h2>Unsere Werte </h2>>ul>>li>>strong>Quality First:</strong> Wir tragen nur Produkte, an die wir glauben und uns getestet haben. Jeder Artikel in unserem Shop erf\u00fcllt unsere hohen Qualit\u00e4ts-, Sicherheits- und Wirksamkeitsstandards.</li><li>strong>Diversity & Inclusion:</strong> Sch\u00f6nheit kommt in allen Formen, und wir feiern jeden von ihnen. Unsere Produktauswahl spiegelt die vielf\u00e4ltigen Bed\u00fcrfnisse unserer Gemeinschaft wider.</li>li>>strong>Gemeinschaftsunterst\u00fctzung:</strong> Wir unterst\u00fctzen Schwarze Unternehmen, Frauenmarken und lokale Unternehmer. Ein Teil unserer Erl\u00f6se geht an Gemeinschaftsinitiativen.</li>li>>strong>Sustainability:</strong> Wir arbeiten mit umweltbewussten Marken zusammen und arbeiten kontinuierlich daran, unseren \u00f6kologischen Fu\u00dfabdruck durch verantwortungsvolle Verpackungs- und Versandpraktiken zu minimieren.</li>li>strong> Wir glauben an die St\u00e4rkung unserer Kunden mit Wissen. Unser Blog, Tutorials und Kundendienstteam sind hier, um Ihnen zu helfen, fundierte Entscheidungen zu treffen.</li>/ul>"}, {"type": "reich_text", "content": "<h2>Warum w\u00e4hlen Bakalr Boutique?</h2>ul>>>li>>strong>Curr Selection:</strong> Jedes Produkt wird von unserem Team von Sch\u00f6nheitsexperten </li>li>>strong>Authentic Products:</strong> 100% echte Produkte direkt von autorisierten H\u00e4ndlern </li>li>>strong> Expertenunterst\u00fctzung:</strong> Unser kompetentes Team ist hier, um Ihnen zu helfen, die perfekten Produkte</li>li><strong>Fast Shipping:</strong> zu finden Die meisten Bestellungen versenden innerhalb von 2448 Stunden</li>li>strong> Easy Returns:</strong> 30-day hasle-free return policy</li>li>>>strong>Loyalty Rewards:</strong> Anmerkungen zu jedem Kauf und entsperren exklusive Perks</li>/ul>"}, {"type": "cta", "heading": "Bereit, Ihre Sch\u00f6nheitsroutine zu transformieren?", "subheading": "Entdecken Sie unsere kuratierte Kollektion von Premiumprodukten", "button_text": "Jetzt kaufen", "button_url": "/products", "secondary_button_text": "Kontaktieren Sie uns", "secondary_button_url": "/contact", "background_color": "bg-prim\u00e4r", "text_color": "text-wei\u00df"}]}', 'completed', NULL, 'libretranslate', 0.95, false, 3, '2025-12-05 03:20:43.240001+00', '2025-12-08 02:00:23.416144+00', '45c1f2c1-cb1a-4694-89ed-2033c255b93a', 'f6910471-d67a-4890-9b53-39548be4b224', '2e87ede3-3b54-4690-ab93-70ca567d6d75');
INSERT INTO public.translations (translated_data, status, source_locale, translation_service, quality_score, is_manual, version, created_at, updated_at, id, content_entry_id, locale_id) VALUES ('{"page_key": "about", "template": "landing", "meta_title": "About Us - Bakalr Boutique | Our Story & Mission", "meta_description": "Learn about Bakalr Boutique - your trusted destination for premium hair care, beauty products, and accessories. Discover our mission to celebrate beauty in all its forms.", "sections": [{"type": "h\u00e9roe", "banner_key": "h\u00e9roe", "full_width": true}, {"type": "rich_text", "content": "Se fund\u00f3 con una misi\u00f3n sencilla pero poderosa: hacer que los productos de belleza premium sean accesibles para todos mientras celebramos la diversa belleza de nuestra comunidad. Lo que comenz\u00f3 como un peque\u00f1o negocio familiar en 2020 se ha convertido en un destino confiable para el cuidado del cabello, cuidado de la piel y esenciales de belleza. Nos dimos cuenta de una brecha en el mercado de productos de calidad que atienden a diversos tipos de cabello y tonos de piel, y nos propusimos llenarlo. Hoy, servimos con orgullo a clientes de todo Estados Unidos e internacional, ofreciendo productos cuidadosamente curados de marcas establecidas e innovadores emergentes en la industria de la belleza"}, {"type": "rich_text", "content": "> > > > Para empoderar a cada individuo para abrazar su belleza \u00fanica a trav\u00e9s del acceso a productos de belleza de primera, diversa y \u00e9ticamente de origen.\" Creemos que la belleza no tiene una sola definici\u00f3n. Viene en todos los colores, texturas y formas. Nuestra misi\u00f3n es proporcionar los productos, la educaci\u00f3n y el apoyo que le ayuden a buscar y sentir lo mejor que sea para usted"}, {"type": "rich_text", "content": "\u05d9h2]Nuestros Valores identificados/h2 instruccionesul especificadoli implicado inicialmente: S\u00f3lo llevamos productos en los que creemos y nos hemos probado. Cada art\u00edculo de nuestra tienda cumple con nuestros altos est\u00e1ndares de calidad, seguridad y eficacia. La belleza viene en todas las formas, y celebramos cada una de ellas. Nuestra selecci\u00f3n de productos refleja las diversas necesidades de nuestra comunidad. Apoyamos activamente a empresas de propiedad negra, marcas de propiedad de mujeres y empresarios locales. Una parte de nuestro producto va a las iniciativas comunitarias. Nos asociamos con marcas ecol\u00f3gicas y trabajamos continuamente para minimizar nuestra huella ambiental a trav\u00e9s de pr\u00e1cticas de embalaje y env\u00edo responsables. Creemos en empoderar a nuestros clientes con conocimiento. Nuestro blog, tutoriales y equipo de atenci\u00f3n al cliente est\u00e1n aqu\u00ed para ayudarle a tomar decisiones informadas"}, {"type": "rich_text", "content": "\u00bfPor qu\u00e9 elegir la boutique Bakalr? Cada producto es escogido a mano por nuestro equipo de expertos en belleza realizados/li t\u00edtulo implicado fieltrong Fuerteng Productos Authentic: realizados/strong confianza 100% productos genuinos provenientes directamente de distribuidores autorizados Soporte de expertos: Nuestro equipo de conocimiento est\u00e1 aqu\u00ed para ayudarle a encontrar los productos perfectos realizados/li t\u00edtulo recomendadoli implicado fuestrong confianzaFast Shipping: La mayor\u00eda de las \u00f3rdenes se env\u00edan dentro de 2448 horas Retorno f\u00e1cil: se realiz\u00f3/fuerte confianza pol\u00edtica de devoluci\u00f3n sin problemas de 30 d\u00edas: se realiz\u00f3 / se obtuvo fieltrong confianzaRecompensas de lealtad: Ganar puntos en cada compra y desbloquear perks exclusivos"}, {"type": "cta", "heading": "\u00bfListo para transformar tu rutina de belleza?", "subheading": "Descubra nuestra colecci\u00f3n curada de productos premium", "button_text": "Compra ahora", "button_url": "/products", "secondary_button_text": "Cont\u00e1ctenos", "secondary_button_url": "/contact", "background_color": "bg-primary", "text_color": "text-white"}]}', 'completed', NULL, 'libretranslate', 0.95, false, 3, '2025-12-05 03:20:43.240001+00', '2025-12-08 02:00:23.416144+00', '4eb1af8c-0766-4c39-a14f-404d2c3189b2', 'f6910471-d67a-4890-9b53-39548be4b224', '3c424c7b-6a9e-48ac-b66a-e8972087b9ae');
INSERT INTO public.translations (translated_data, status, source_locale, translation_service, quality_score, is_manual, version, created_at, updated_at, id, content_entry_id, locale_id) VALUES ('{"page_key": "about", "template": "landing", "meta_title": "About Us - Bakalr Boutique | Our Story & Mission", "meta_description": "Learn about Bakalr Boutique - your trusted destination for premium hair care, beauty products, and accessories. Discover our mission to celebrate beauty in all its forms.", "sections": [{"type": "h\u00e9ros", "banner_key": "h\u00e9ros-about", "full_width": true}, {"type": "texte_rich", "content": "<h2>Qui sommes-nous</h2><p>Bakalr Boutique a \u00e9t\u00e9 fond\u00e9e avec une mission simple et puissante : rendre les produits de beaut\u00e9 haut de gamme accessibles \u00e0 tous tout en c\u00e9l\u00e9brant la beaut\u00e9 diversifi\u00e9e de notre communaut\u00e9.</p><p> Ce qui a commenc\u00e9 en 2020 en tant que petite entreprise familiale est devenu une destination de confiance pour les soins capillaires, les soins de la peau et l''essentiel de beaut\u00e9. Nous avons remarqu\u00e9 une lacune dans le march\u00e9 des produits de qualit\u00e9 qui r\u00e9pondent \u00e0 divers types de cheveux et de tons de peau, et nous nous sommes mis \u00e0 le combler.</p><p> Aujourd''hui, nous servons fi\u00e8rement nos clients aux \u00c9tats-Unis et \u00e0 l''\u00e9tranger, offrant des produits soigneusement s\u00e9lectionn\u00e9s provenant de marques \u00e9tablies et d''innovateurs \u00e9mergents de l''industrie de la beaut\u00e9.</p>"}, {"type": "texte_rich", "content": "<h2>Notre mission</h2><p><em>\"Permettre \u00e0 chaque personne d''embrasser sa beaut\u00e9 unique en acc\u00e9dant \u00e0 des produits de beaut\u00e9 de qualit\u00e9 sup\u00e9rieure, diversifi\u00e9s et d''origine \u00e9thique.\"</em></p><p> Nous pensons que la beaut\u00e9 n''a pas de d\u00e9finition unique. Il vient dans toutes les couleurs, textures et formes. Notre mission est de fournir les produits, l''\u00e9ducation et le soutien qui vous aident \u00e0 regarder et \u00e0 sentir votre mieux, quoi que cela signifie pour vous.</p>"}, {"type": "texte_rich", "content": "<h2>Nos valeurs</h2><ul><li><strong>Qualit\u00e9 d''abord:</strong> Nous ne portons que des produits auxquels nous croyons et nous avons test\u00e9 nous-m\u00eames. Chaque article de notre magasin r\u00e9pond \u00e0 nos normes \u00e9lev\u00e9es de qualit\u00e9, d''innocuit\u00e9 et d''efficacit\u00e9.</li><li><strong>Diversit\u00e9 et inclusion:</strong> La beaut\u00e9 vient sous toutes ses formes, et nous c\u00e9l\u00e9brons chacune d''elles. Notre s\u00e9lection de produits refl\u00e8te les divers besoins de notre collectivit\u00e9.</li><li><strong>Soutien communautaire :</strong> Nous soutenons activement les entreprises appartenant \u00e0 des Noirs, les marques appartenant \u00e0 des femmes et les entrepreneurs locaux. Une partie de nos recettes est consacr\u00e9e aux initiatives communautaires.</li><li><strong> Durabilit\u00e9 :</strong> Nous collaborons avec des marques \u00e9co-conscientes et nous travaillons continuellement \u00e0 minimiser notre empreinte environnementale gr\u00e2ce \u00e0 des pratiques responsables d''emballage et d''exp\u00e9dition.</li><li><strong>\u00c9ducation:</strong> Nous croyons en l''autonomisation de nos clients avec la connaissance. Notre blog, nos tutoriels et notre \u00e9quipe de service \u00e0 la client\u00e8le sont l\u00e0 pour vous aider \u00e0 prendre des d\u00e9cisions \u00e9clair\u00e9es.</li></ul>"}, {"type": "texte_rich", "content": "<h2>Pourquoi choisir la boutique Bakalr?</h2><ul><li><strong>Cured Selection:</strong> Chaque produit est s\u00e9lectionn\u00e9 \u00e0 la main par notre \u00e9quipe d''experts en beaut\u00e9</li><li><strong>Produits authentiques :</strong> Produits 100% authentiques provenant directement de distributeurs autoris\u00e9s</li><li><strong> Soutien d''experts :</strong> Notre \u00e9quipe comp\u00e9tente est ici pour vous aider \u00e0 trouver les produits parfaits</li><li><strong>Exp\u00e9dition rapide:</strong> La plupart des commandes sont exp\u00e9di\u00e9es dans les 24-48 heures</li><li><strong> Retours faciles :</strong> Politique de retour sans tracas de 30 jours</li><li><strong>R\u00e9compenses de fid\u00e9lit\u00e9 :</strong> Gagnez des points sur chaque achat et d\u00e9bloquez les avantages exclusifs</li></ul>"}, {"type": "cta", "heading": "Pr\u00eat \u00e0 transformer votre routine esth\u00e9tique ?", "subheading": "D\u00e9couvrez notre collection de produits haut de gamme", "button_text": "Achetez maintenant", "button_url": "/products", "secondary_button_text": "Contactez-nous", "secondary_button_url": "/contact", "background_color": "bg-primaire", "text_color": "blanc texte"}]}', 'completed', NULL, 'libretranslate', 0.95, false, 3, '2025-12-05 03:20:43.240001+00', '2025-12-08 02:00:23.416144+00', 'f2632a0c-507d-4b5e-8851-752a2a17b8b1', 'f6910471-d67a-4890-9b53-39548be4b224', '8f36bfaa-ef7f-482a-ade0-8932044ec4a5');
